import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";

function Expenses(props) {
  return (
    <Card className="expenses">
      {/* {console.log(props)}
    {console.log('test')} */}

      {props.items.map((item) => {
        return (
          <ExpenseItem
          // We should always add a key value to help react rendering the elements in more efficient way.
            key = {item.id}
            title={item.title}
            amount={item.amount}
            date={item.date}
          ></ExpenseItem>
        );
      })}

      {/*
